mapper.update:
UPDATE nv_test_grp
SET test_grp_nm = #{testGrpNm, jdbcType=VARCHAR}
WHERE test_grp_cd = #{testGrpCd}

UPDATE nv_test_grp
SET active_yn = 'N'
WHERE test_grp_cd = #{testGrpCd}
AND 0 = (
    SELECT count(*)
    FROM nv_test_grp
    WHERE active_yn = 'Y'
    AND test_supragrp_cd = #{testGrpCd}
)

mapper.select:
SELECT test_grp_cd, test_grp_nm, coalesce(test_supragrp_cd,0) test_supragrp_cd , (SELECT test_grp_nm FROM nv_test_grp WHERE test_grp_cd = a.test_supragrp_cd) test_supragrp_nm FROM nv_test_grp a WHERE user_id = #{userId} AND active_yn = 'Y'
AND test_grp_cd = #{testGrpCd}
AND test_supragrp_cd IS NULL
