mapper.insert:
INSERT INTO nvecaremsg (
    ecare_no, user_id, grp_cd, ecare_nm, ecare_sts, create_dt, create_tm, lastupdate_dt, campaign_type, ecmschedule_no
  , sending_mode, retry_cnt, ecare_class, channel_type, service_type, ecare_level, svc_id, sub_type, scenario_no, mail_type
  , req_dept_id, req_user_id, resend_ecare_no
   , segment_no
) VALUES (
    #{ecareNo}, #{userId, jdbcType=VARCHAR}, #{grpCd, jdbcType=VARCHAR}, #{ecareNm, jdbcType=VARCHAR}, 'I', #{createDt, jdbcType=CHAR}, #{createTm, jdbcType=CHAR}, #{lastUpdateDt, jdbcType=CHAR}, 'Y', #{ecmScheduleNo}
  , #{sendingMode, jdbcType=CHAR}, #{retryCnt}, 'A', #{channelType, jdbcType=CHAR}, #{serviceType, jdbcType=CHAR}, '1', #{svcId, jdbcType=VARCHAR}, #{subType, jdbcType=CHAR}, #{scenarioNo}, #{mailType, jdbcType=CHAR}
  , #{reqDept, jdbcType=VARCHAR}, #{reqUser, jdbcType=VARCHAR}, #{resendEcareNo}
  , #{segmentNo, jdbcType=INTEGER}
)

mapper.update:
UPDATE nvecaremsg
SET
   lastupdate_dt = to_char(sysdate, 'yyyyMMdd')
  , ecare_nm = #{ecareNm, jdbcType=VARCHAR}
  , ecare_desc = #{ecareDesc, jdbcType=VARCHAR}
  , lastupdate_dt = #{lastUpdateDt, jdbcType=CHAR}
  , lastupdate_tm = #{lastUpdateTm, jdbcType=CHAR}
  , target_cnt = #{targetCnt}
  , sender_nm = #{senderNm, jdbcType=VARCHAR}
  , sender_email = #{senderEmail, jdbcType=VARCHAR}
  , sending_mode = #{sendingMode, jdbcType=CHAR}
  , retry_cnt = #{retryCnt, jdbcType=NUMERIC}
  , sender_tel = #{senderTel, jdbcType=VARCHAR}
  , open_type = #{emailOpenType, jdbcType=VARCHAR}
  , deploy_type = #{deployType, jdbcType=VARCHAR}
  , retmail_receiver = #{retmailReceiver, jdbcType=VARCHAR}
  , slot1_field = #{slot1Field, jdbcType=CHAR}
  , slot2_field = #{slot2Field, jdbcType=CHAR}
  , slot3_field = #{slot3Field, jdbcType=CHAR}
  , slot4_field = #{slot4Field, jdbcType=CHAR}
  , slot5_field = #{slot5Field, jdbcType=CHAR}
  , slot6_field = #{slot6Field, jdbcType=CHAR}
  , slot7_field = #{slot7Field, jdbcType=CHAR}
  , slot8_field = #{slot8Field, jdbcType=CHAR}
  , slot9_field = #{slot9Field, jdbcType=CHAR}
  , slot10_field = #{slot10Field, jdbcType=CHAR}
  , kakao_sender_key = #{kakaoSenderKey, jdbcType=VARCHAR}
  , kakao_tmpl_cd = #{kakaoTmplCd, jdbcType=VARCHAR}
  , kakao_image_no = #{kakaoImageNo}
  , template_sender_key = #{templateSenderKey, jdbcType=VARCHAR}
  , failback_send_yn = #{failbackSendYn, jdbcType=CHAR}
  , ecare_preface = #{ecarePreface, jdbcType=VARCHAR}
  , req_dept_id = #{reqDept, jdbcType=VARCHAR}
  , failback_subject = #{failbackSubject, jdbcType=VARCHAR}
  , failback_subject = ''
  , ecare_class = #{ecareClass, jdbcType=VARCHAR}
WHERE ecare_no = #{ecareNo}

UPDATE nvecaremsg
SET sending_mode = 'T'
WHERE ecare_no = #{ecareNo}

UPDATE nvecaremsg
SET verify_yn = #{verifyYn, jdbcType=CHAR}, verify_grp_cd = #{verifyGrpCd, jdbcType=VARCHAR}
WHERE ecare_no = #{ecareNo}

UPDATE nvecaremsg
SET ecare_sts = #{ecareSts, jdbcType=CHAR}
WHERE ecare_no = #{ecareNo}

UPDATE nvecaremsg
SET ecare_sts = #{ecareSts, jdbcType=CHAR}
WHERE ecare_sts = #{prevEcareSts}

UPDATE nvecaremsg
SET
    segment_no = NULL
    segment_no = #{segmentNo}
WHERE ecare_no = #{ecareNo}
AND segment_no IS NULL

UPDATE nvecaremsg
SET segment_no = #{segmentNo}, target_cnt = #{segmentSize}, relation_type = #{ecareVo.relationType, jdbcType=CHAR}
WHERE ecare_no = #{ecareVo.newEcareNo}

UPDATE nvecaremsg
SET
    template_type = #{templateType}
  , ecare_preface = #{ecarePreface, jdbcType=VARCHAR}
  , kakao_sender_key = #{kakaoSenderkey, jdbcType=VARCHAR}
  , kakao_tmpl_cd = #{kakaoTemplateCd, jdbcType=VARCHAR}
  , kakao_image_no = #{kakaoImageNo}
  , tmpl_ver = #{tmplVer}
  , cover_ver = #{coverVer}
  , handler_ver = #{handlerVer}
  , handler_seq = #{handlerSeq}
WHERE ecare_no = #{no}

mapper.select:
SELECT ecare_preface, sender_tel, kakao_sender_key, kakao_tmpl_cd, failback_send_yn, failback_subject, kakao_image_no, ecare_class, channel_type
FROM nvecaremsg
WHERE ecare_no = #{ecareNo}

SELECT coalesce(ecmschedule_no,0) ecmschedule_no, user_id, service_type
FROM nvecaremsg
WHERE ecare_no = #{ecareNo}

SELECT ecare_no, coalesce(ecmschedule_no,0) ecmschedule_no, service_type, sub_type, coalesce(segment_no,0) segment_no, ecare_sts
FROM nvecaremsg
WHERE ecare_no = #{ecareNo}

SELECT ecare_no, scenario_no, ecare_nm, channel_type, depth_no, relation_type
FROM nvecaremsg
WHERE scenario_no = #{scenarioNo}
AND depth_no = #{ecareVo.depthNo}
AND relation_tree LIKE ((SELECT relation_tree FROM nvecaremsg WHERE ecare_no = #{ecareVo.ecareNo}) || '%')

SELECT ecare_nm
FROM nvecaremsg
WHERE ecare_no = #{ecareNo}

SELECT retmail_receiver
FROM nvecaremsg
WHERE ecare_no IN (
    SELECT max(ecare_no)
    FROM nvecaremsg
    WHERE scenario_no = #{scenarioNo}
    AND channel_type = #{ecareVo.channelType}
)

SELECT sender_email
FROM nvecaremsg
WHERE ecare_no IN (
    SELECT max(ecare_no)
    FROM nvecaremsg
    WHERE scenario_no = #{scenarioNo}
    AND channel_type = #{ecareVo.channelType}
)

SELECT sender_tel
FROM nvecaremsg
WHERE ecare_no IN (
    SELECT max(ecare_no)
    FROM nvecaremsg
    WHERE scenario_no IN (
        SELECT scenario_no
        FROM nvecarescenario
        WHERE user_id = #{userId}
        AND grp_cd = #{grpCd}
        AND tag_no = #{tagNo}
    )
    AND channel_type = #{ecareVo.channelType}
    AND sender_tel IS NOT NULL
)

SELECT sender_nm
FROM nvecaremsg
WHERE ecare_no IN (
    SELECT max(ecare_no)
    FROM nvecaremsg
    WHERE scenario_no IN (
        SELECT scenario_no
        FROM nvecarescenario
        WHERE user_id = #{userId}
        AND grp_cd = #{grpCd}
        AND tag_no = #{tagNo}
    )
    AND channel_type = #{ecareVo.channelType}
    AND sender_nm IS NOT NULL
)

SELECT coalesce(resend_ecare_no,0)
FROM nvecaremsg
WHERE ecare_no = #{ecareNo}

SELECT count(*)
FROM nvecaremsg
WHERE svc_id = #{svcId}

SELECT ecare_no
FROM nvecaremsg
WHERE svc_id = #{svcId}
AND user_id = #{userId}
AND service_type = 'R'

SELECT channel_type
FROM nvecaremsg
WHERE ecare_no = #{ecareNo}

SELECT count(*) FROM nvecarescenario a, nvecaremsg b, nvuser c, ( SELECT ecare_no FROM nvecaresendresult WHERE result_sts = 'XE'
AND start_dt >= #{searchQstartDt}
AND start_dt <= #{searchQendDt}
GROUP BY ecare_no ) d WHERE a.scenario_no = b.scenario_no AND b.user_id = c.user_id AND b.grp_cd = c.grp_cd AND b.ecare_no = d.ecare_no

SELECT * FROM ( SELECT row_number() OVER (ORDER BY ecare_no DESC) as rnum , scenario_no, scenario_nm, user_id, grp_cd, channel_type, ecare_no, ecare_preface, ecare_sts, coalesce(target_cnt,0) target_cnt, coalesce(depth_no,0) depth_no , ecare_sts_nm, start_tm, name_kor, grp_nm FROM ( SELECT a.scenario_no, a.scenario_nm, a.user_id, a.grp_cd, b.channel_type, b.ecare_no, b.ecare_preface, b.ecare_sts, b.target_cnt, b.depth_no , (SELECT val FROM nv_cd_mst WHERE cd_cat = '200111' AND cd = b.ecare_sts AND lang = #{language}) as ecare_sts_nm , coalesce((SELECT invoke_tm FROM nvecmschedule WHERE ecmschedule_no = b.ecmschedule_no), '') as start_tm , (SELECT name_kor FROM nvuser WHERE user_id = a.user_id) as name_kor , (SELECT grp_nm FROM nvusergrp WHERE grp_cd = a.grp_cd) as grp_nm FROM nvecarescenario a, nvecaremsg b WHERE a.scenario_no = b.scenario_no AND 1 = ( SELECT 1 FROM nvecaresendresult c WHERE ecare_no = b.ecare_no AND result_sts = 'XE'
AND start_dt >= #{searchQstartDt}
AND start_dt <= #{searchQendDt}
FETCH FIRST 1 ROWS ONLY )
) d ORDER BY scenario_no DESC, ecare_no ) e WHERE rnum BETWEEN #{startRow} AND #{endRow}
