mapper.select:
SELECT count(*) FROM nvmobilecontents
WHERE file_type IN ('T','D')
WHERE file_type = #{fileType}

SELECT user_id, conts_no, conts_nm, file_path, file_name, file_type, conts_txt, file_preview_path, file_preview_name, file_size , to_char(to_date(create_dt,'YYYYMMDD'),'YYYY-MM-DD') create_dt, to_char(to_date(create_tm,'HH24MISS'),'HH:MI:SS') create_tm FROM ( SELECT row_number() OVER (ORDER BY conts_no DESC) as rnum , user_id, conts_no, conts_nm, file_path, file_name, file_type, conts_txt, file_preview_path, file_preview_name, file_size , create_dt, create_tm FROM nvmobilecontents
WHERE file_type IN ('T','D')
WHERE file_type = #{fileType}
ORDER BY conts_no DESC ) a WHERE rnum BETWEEN #{startRow} AND #{endRow}

