mapper.select:
SELECT user_id, conts_no, conts_nm, file_path, file_name, file_type, conts_txt, file_preview_path, file_preview_name, file_size , kakao_sender_key, kakao_tmpl_cd, image_link, kakao_buttons, content_type, unsubscribe_content, message_type , to_char(to_date(create_dt, 'YYYYMMDD'), 'YYYY-MM-DD') as create_dt, to_char(to_date(create_tm, 'HH24MISS'), 'HH:MI:SS') as create_tm , (SELECT kakao_yellow_id FROM nvkakaoprofile WHERE user_id = #{userId} AND kakao_sender_key = a.kakao_sender_key) as kakao_yellow_id , kakao_tmpl_msg_type, kakao_security_yn, kakao_em_type, kakao_quick_replies FROM ( SELECT row_number() OVER (ORDER BY conts_no DESC) as rnum , user_id, conts_no, conts_nm, file_path, file_name, file_type, conts_txt, file_preview_path, file_preview_name, file_size , kakao_sender_key, kakao_tmpl_cd, image_link, kakao_buttons, content_type, unsubscribe_content, message_type, create_dt, create_tm , kakao_tmpl_msg_type, kakao_security_yn, kakao_em_type, kakao_quick_replies FROM nvmobilecontents a
WHERE file_type IN ('T','D')
WHERE file_type = #{fileType}
AND use_yn = 'Y' ORDER BY conts_no DESC ) a WHERE rnum BETWEEN #{startRow} AND #{endRow}

SELECT user_id, conts_no, conts_nm, file_path, file_name, file_type, conts_txt, file_preview_path, file_preview_name, file_size , kakao_sender_key, kakao_tmpl_cd, image_link, kakao_buttons, content_type, unsubscribe_content, message_type , to_char(to_date(create_dt, 'YYYYMMDD'), 'YYYY-MM-DD') as create_dt, to_char(to_date(create_tm, 'HH24MISS'), 'HH:MI:SS') as create_tm , (SELECT kakao_yellow_id FROM nvkakaoprofile WHERE user_id = #{userId} AND kakao_sender_key = a.kakao_sender_key) as kakao_yellow_id FROM nvmobilecontents a
WHERE file_type IN ('T','D')
WHERE file_type = #{fileType}

