mapper.select:
SELECT coalesce(conts_txt,'') conts_txt, kakao_buttons
FROM nvmobilecontents
WHERE conts_no = #{contsNo}

SELECT * FROM ( SELECT row_number() OVER (ORDER BY a.conts_no DESC) as rnum , a.conts_no, a.conts_nm, a.conts_desc, a.file_type, a.auth_type, a.tag_no, a.conts_txt, a.kakao_sender_key, a.kakao_tmpl_cd, a.kakao_insp_status , a.use_yn, b.user_id, b.name_kor as user_nm, b.grp_cd, c.grp_nm , to_char(to_date(a.create_dt,'YYYYMMDD'),'YYYY-MM-DD') as create_dt , to_char(to_date(a.create_tm,'HH24MISS'),'HH:MI:SS') as create_tm , (SELECT tag_nm FROM nvmobilecontentstag WHERE tag_no = a.tag_no) as tag_nm , (SELECT kakao_yellow_id FROM nvkakaoprofile WHERE user_id = #{userId} AND kakao_sender_key = a.kakao_sender_key) as kakao_yellow_id , (SELECT val FROM nv_cd_mst WHERE cd_cat = 'KA011' AND cd = a.kakao_insp_status) as kakao_insp_status_nm FROM nvmobilecontents a, nvuser b, nvusergrp c WHERE a.user_id = b.user_id AND b.grp_cd = c.grp_cd AND a.file_type = 'A'
AND use_yn = #{useYn}
ORDER BY a.conts_no DESC ) b WHERE rnum BETWEEN #{startRow} AND #{endRow}

if.if:
AND (
