mapper.select:
SELECT srfidd
FROM nvsendlog
WHERE campaign_no = #{serviceNo}
AND seq = #{seq}

SELECT x.error_cd, coalesce(x.send_cnt,0) send_cnt, coalesce(x.rnum,0) rnum, coalesce(x.gid,0) gid , (CASE x.rnum WHEN 0 THEN y.val END) error_gubun , (CASE x.gid WHEN 1 THEN 'Sub Total' WHEN 3 THEN 'Total' ELSE x.val END) error_msg FROM ( SELECT c.cd_cat error_gubun, a.error_cd, count(a.error_cd) send_cnt, max(c.val) error_msg , max(coalesce(c.val, '알수없는 에러코드')) val , max(coalesce(c.val, 'unknown error code')) val , grouping_id(c.cd_cat, a.error_cd) gid , row_number() OVER(PARTITION BY c.cd_cat ORDER BY c.cd_cat) rnum FROM nvsendlog a, nvcampaign b, nv_cd_mst c WHERE a.campaign_no = b.campaign_no AND a.error_cd = c.cd AND a.campaign_no = #{campaignNo}
AND a.error_cd NOT IN (#{successCode})
AND b.channel_type = 'P' AND c.par_cd_cat LIKE 'AP00%' AND c.lang = #{lang} GROUP BY ROLLUP(c.cd_cat, a.error_cd) ) x LEFT OUTER JOIN nv_cd_mst y ON x.error_gubun = y.cd_cat AND y.par_cd_cat = 'AP00' AND y.lang = #{lang} ORDER BY CASE WHEN x.error_gubun IS NULL THEN 2 ELSE 1 END, x.error_gubun, x.gid, x.rnum

SELECT * FROM ( SELECT row_number() OVER (ORDER BY a.campaign_no DESC) as rnum , a.campaign_nm, coalesce(b.fail_back_channel, a.channel_type) as msg_type , a.sender_email as sender , a.sender_tel as sender , b.customer_nm, b.customer_email, b.send_dt, b.send_tm, b.error_cd, b.err_msg, b.campaign_no, b.result_seq, b.list_seq, b.record_seq, b.customer_key , b.slot1, b.slot2, b.req_user_id, b.req_dept_id, coalesce(b.open_dt,'') open_dt , b.customer_key as send_seq, b.part_message as snd_msg, b.fail_back_result_cd sms_rslt_cd, coalesce(c.resend_sts,' ') resend_sts , CASE WHEN b.open_dt IS NULL THEN 'N' ELSE 'Y' END as flag , (SELECT cd_desc FROM nv_cd_mst WHERE cd_cat IN ('AS0001','AS0002','AS0003') AND cd = b.fail_back_result_cd AND lang = #{userVo.language}) as sms_rslt_msg
FROM nvcampaign a, nvsendlog b, nvsendresult c WHERE a.campaign_no = b.campaign_no AND b.campaign_no = c.campaign_no AND b.result_seq = c.result_seq
) d WHERE rnum >= ((#{nowPage} - 1) * #{countPerPage} + 1) AND rnum <= #{nowPage} * #{countPerPage}

SELECT
    a.campaign_nm as service_nm, a.channel_type, a.ab_test_type
  , b.campaign_no as service_no, b.customer_email, b.customer_nm, b.customer_key, b.error_cd, b.err_msg, b.send_dt, b.send_tm, b.result_seq, b.list_seq
  , b.record_seq, b.seq as seq, b.slot1, b.slot2, b.tmpl_cd, coalesce(c.resend_sts,' ') resend_sts, c.super_seq, ' ' as service_type, 's' as sub_type, ' ' resend_reason
  , coalesce((SELECT file_preview_path FROM nvmobilecontents WHERE conts_no = a.kakao_image_no),' ') as file_preview_path

FROM nvcampaign a, nvsendlog b, nvsendresult c
WHERE a.campaign_no = b.campaign_no
AND b.campaign_no = c.campaign_no
AND b.result_seq = c.result_seq
AND a.campaign_no = #{serviceNo}
AND a.channel_type = #{channel}
AND b.result_seq = #{resultSeq}
AND b.list_seq = #{listSeq}

SELECT customer_email, customer_nm, customer_key, (send_dt + send_tm) send_dtm
FROM nvsendlog
WHERE campaign_no = #{campaignNo}
AND result_seq = #{resultSeq}
AND error_cd = #{errorCd}
