sql.if:
AND a.channel_type = #{searchChannel}

AND b.report_dt LIKE concat(#{searchDate},'%')


AND b.start_dt LIKE concat(#{searchStartDate},'%')

mapper.select:
SELECT report_dt as start_dt , coalesce(sum(success_cnt),0) as success_cnt, coalesce(sum(fail_cnt),0) as soft_bounce_cnt , coalesce(sum(sms_success_cnt),0) as sms_success_cnt, coalesce(sum(sms_fail_cnt),0) as sms_fail_cnt , coalesce(sum(lms_success_cnt),0) as lms_success_cnt, coalesce(sum(lms_fail_cnt),0) as lms_fail_cnt FROM nvecaremsg a, nvkakaorptsendresult b WHERE a.ecare_no = b.service_no AND b.service_no = #{ecare_no} AND b.report_dt = #{report_dt}
AND b.result_seq = #{result_seq}
GROUP BY report_dt
