mapper.select:
SELECT count(*)
FROM nvsendresult
WHERE campaign_no = #{serviceNo}
AND result_seq = #{date}

SELECT result_desc
FROM nvsendresult
WHERE campaign_no = #{campaignNo}
AND result_seq = #{resultSeq}
AND resend_sts IS NULL

SELECT coalesce(max(b.target_cnt),0) target_cnt, coalesce(sum(b.send_cnt),0) send_cnt, coalesce(sum(b.success_cnt),0) success_cnt , coalesce(min(b.smtp_except_cnt + b.no_route_cnt + b.refused_cnt + b.etc_except_cnt),0) soft_bounce , coalesce(sum(b.unknown_user_cnt + b.unknown_host_cnt + b.invalid_address_cnt),0) hard_bounce FROM nvcampaign a, nvsendresult b WHERE a.campaign_no = b.campaign_no AND a.scenario_no = #{scenarioNo}
AND a.relation_type IN ('N', 'R', 'L')
AND a.campaign_no = #{campaignNo}
AND b.result_sts = 'XE' AND (b.resend_sts IS NULL OR b.resend_sts = 'F')

SELECT a.scenario_no, a.scenario_nm, b.campaign_no, b.campaign_preface, b.channel_type , b.relation_type, coalesce(b.depth_no,0) depth_no, c.log_sts log_sts, c.start_dt as sendstart_dt, c.start_tm as sendstart_tm , coalesce(c.target_cnt,0) target_cnt, coalesce(c.send_cnt,0) send_cnt, coalesce(c.success_cnt,0) success_cnt , coalesce(c.open_cnt,0) open_cnt, coalesce(c.duration_cnt,0) duration_cnt, coalesce(c.returnmail_cnt,0) returnmail_cnt , coalesce(c.smtp_except_cnt + c.no_route_cnt + c.refused_cnt + c.etc_except_cnt,0) soft_bounce_cnt , coalesce(c.unknown_user_cnt + c.unknown_host_cnt + c.invalid_address_cnt,0) hard_bounce_cnt, c.result_seq FROM nvscenario a, nvcampaign b, nvsendresult c WHERE result_sts = 'XE' AND a.scenario_no = b.scenario_no AND b.campaign_no = c.campaign_no AND a.scenario_no = #{scenarioNo}
AND b.relation_type IN ('N', 'R', 'L')
AND b.campaign_no = #{campaignNo}
AND c.resend_sts is null ORDER BY a.scenario_no, b.campaign_no
