mapper.insert:
INSERT INTO nvecarescenario (
    scenario_no, user_id, grp_cd, scenario_nm, scenario_desc, create_dt, create_tm, lastupdate_dt, lastupdate_tm, tag_no
  , finish_yn, service_type, sub_type, handler_type, chrg_nm, brc_nm
) VALUES (
    #{scenarioNo}, #{userId, jdbcType=VARCHAR}, #{grpCd, jdbcType=VARCHAR}, #{scenarioNm, jdbcType=VARCHAR}, #{scenarioDesc, jdbcType=VARCHAR}, #{createDt, jdbcType=CHAR}, #{createTm, jdbcType=CHAR}, #{lastUpdateDt, jdbcType=CHAR}, #{lastUpdateTm, jdbcType=CHAR}, #{tagNo}
  , 'N', #{serviceType, jdbcType=CHAR}, #{subType, jdbcType=CHAR}, #{handlerType, jdbcType=CHAR}, #{chrgNm, jdbcType=VARCHAR}, #{brcNm, jdbcType=VARCHAR}
)

mapper.update:
UPDATE nvecarescenario
SET scenario_nm = #{ecareVo.ecareNm, jdbcType=VARCHAR}
  , chrg_nm = #{chrgNm, jdbcType=VARCHAR}
  , brc_nm = #{brcNm, jdbcType=VARCHAR}
  , lastupdate_dt = #{lastUpdateDt, jdbcType=CHAR}
  , lastupdate_tm = #{lastUpdateTm, jdbcType=CHAR}
WHERE scenario_no = #{scenarioNo}

UPDATE nvecarescenario
SET
    lastupdate_dt = #{lastUpdateDt, jdbcType=CHAR}
  , lastupdate_tm = #{lastUpdateTm, jdbcType=CHAR}
  , finish_yn = #{finishYn, jdbcType=CHAR}
  , finish_dt = #{finishDt, jdbcType=CHAR}
WHERE scenario_no = #{scenarioNo}

mapper.delete:
DELETE FROM nvecarescenario

WHERE scenario_no IN (

    #{scenarioNo}

)


WHERE scenario_no = #{scenarioNo}

mapper.select:
SELECT scenario_no, user_id, grp_cd, scenario_nm, scenario_desc, scenario_type, create_dt, create_tm, lastupdate_dt, lastupdate_tm , finish_yn, finish_dt, service_type, coalesce(tag_no,0) tag_no, handler_type, chrg_nm, brc_nm, f.ecare_no, coalesce(segment_no,0) segment_no, ecare_nm , ecare_preface, ecare_sts, campaign_type, template_type, coalesce(ecmschedule_no,0) ecmschedule_no, sender_nm, sender_email, sending_mode, coalesce(retry_cnt,1) retry_cnt, receiver_nm , sender_tel, retmail_receiver, channel_type, account_dt, ecare_level, svc_id, sub_type, coalesce(depth_no,1) depth_no, verify_yn, verify_grp_cd , mail_type, template_sender_key, kakao_sender_key, kakao_tmpl_cd, coalesce(kakao_image_no,0) kakao_image_no, failback_send_yn, failback_subject, req_dept_id as req_dept, req_user_id as req_user , name_kor, tag_nm, grp_nm, term_type , (SELECT val FROM nv_cd_mst WHERE cd_cat = '200111' AND cd = f.ecare_sts AND lang = #{userVo.language}) as ecare_sts_nm , (SELECT grp_nm FROM nvusergrp WHERE grp_cd = f.verify_grp_cd) as verify_grp_nm , (SELECT kakao_yellow_id FROM nvkakaoprofile WHERE user_id = f.user_id AND kakao_sender_key = f.kakao_sender_key) kakao_yellow_id FROM ( SELECT a.scenario_no, a.user_id, a.grp_cd, a.scenario_nm, a.scenario_desc, a.scenario_type, a.create_dt, a.create_tm, a.lastupdate_dt, a.lastupdate_tm , a.finish_yn, a.finish_dt, a.service_type, a.tag_no, a.handler_type, a.chrg_nm, a.brc_nm, b.ecare_no, b.segment_no, b.ecare_nm , b.ecare_preface, b.ecare_sts, b.campaign_type, b.template_type, b.ecmschedule_no, b.sender_nm, b.sender_email, b.sending_mode, b.retry_cnt, b.receiver_nm , b.sender_tel, b.retmail_receiver, b.channel_type, b.account_dt, b.ecare_level, b.svc_id, b.sub_type, b.depth_no, b.verify_yn, b.verify_grp_cd , b.mail_type, b.template_sender_key, b.kakao_sender_key, b.kakao_tmpl_cd, b.kakao_image_no, b.failback_send_yn, b.failback_subject, b.req_dept_id, b.req_user_id , c.name_kor, d.tag_nm, e.grp_nm FROM nvecarescenario a, nvecaremsg b, nvuser c, nvecaremsgtag d, nvusergrp e WHERE a.scenario_no = b.scenario_no AND a.user_id = c.user_id AND a.tag_no = d.tag_no AND a.grp_cd = e.grp_cd
AND a.scenario_no = #{scenarioNo}
AND b.ecare_no = #{ecareVo.ecareNo}
AND b.depth_no = #{ecareVo.depthNo}
) f LEFT OUTER JOIN nvecaretraceinfo g ON f.ecare_no = g.ecare_no
