mapper.update:
UPDATE nvscenario
SET
    user_id = #{userId, jdbcType=VARCHAR}
  , grp_cd = #{grpCd, jdbcType=VARCHAR}
  , scenario_nm = #{scenarioNm, jdbcType=VARCHAR}
  , scenario_desc = #{scenarioDesc, jdbcType=VARCHAR}
  , scenario_type = #{scenarioType, jdbcType=CHAR}
  , create_dt = #{createDt, jdbcType=CHAR}
  , create_tm = #{createTm, jdbcType=CHAR}
  , lastupdate_dt = #{lastUpdateDt, jdbcType=CHAR}
  , lastupdate_tm = #{lastUpdateTm, jdbcType=CHAR}
  , finish_yn = #{finishYn, jdbcType=CHAR}
  , finish_dt = #{finishDt, jdbcType=CHAR}
  , tag_no = #{tagNo}
  , handler_type = #{handlerType, jdbcType=CHAR}
WHERE scenario_no = #{scenarioNo}

UPDATE nvscenario
SET
    user_id = #{userId, jdbcType=VARCHAR}
  , grp_cd = #{grpCd, jdbcType=VARCHAR}
  , scenario_nm = #{scenarioNm, jdbcType=VARCHAR}
  , scenario_desc = #{scenarioDesc, jdbcType=VARCHAR}
  , lastupdate_dt = #{lastUpdateDt, jdbcType=CHAR}
  , lastupdate_tm = #{lastUpdateTm, jdbcType=CHAR}
  , tag_no = #{tagNo}
  , handler_type = #{handlerType, jdbcType=CHAR}
WHERE scenario_no = #{scenarioNo}

UPDATE nvscenario
SET
    lastupdate_dt = #{lastUpdateDt, jdbcType=CHAR}
  , lastupdate_tm = #{lastUpdateTm, jdbcType=CHAR}
  , finish_yn = #{finishYn, jdbcType=CHAR}
  , finish_dt = #{finishDt, jdbcType=CHAR}
WHERE scenario_no = #{scenarioNo}

mapper.delete:
DELETE FROM nvscenario

WHERE scenario_no IN (

    #{scenarioNo}

)


WHERE scenario_no = #{scenarioNo}

mapper.select:
SELECT scenario_no, user_id, grp_cd, scenario_nm, scenario_desc, scenario_type, create_dt, create_tm, lastupdate_dt, lastupdate_tm, finish_yn, finish_dt, tag_no, handler_type
FROM nvscenario
WHERE scenario_no = #{scenarioNo}

SELECT a.scenario_no, a.user_id, a.grp_cd, a.scenario_nm, a.scenario_desc, a.scenario_type, a.create_dt, a.create_tm, a.lastupdate_dt, a.lastupdate_tm , a.finish_yn, a.finish_dt, a.tag_no, a.handler_type, b.campaign_no, b.segment_no, b.campaign_nm, b.campaign_desc, b.campaign_preface, b.campaign_sts , b.campaign_type, b.sendstart_dt, b.sendstart_tm, b.target_cnt, b.survey_no, coalesce(b.retry_cnt,1) retry_cnt, b.sender_nm, b.sender_email, b.receiver_nm, b.sender_tel , b.retmail_receiver, b.channel_type, b.promotion_type, b.campaign_level, b.depth_no, b.approval_sts, b.req_dept_id req_dept, b.req_user_id req_user, b.divide_yn, b.divide_interval , coalesce(b.divide_cnt,0) divide_cnt, b.ab_test_type, b.ab_test_cond, coalesce(b.ab_test_rate,0) ab_test_rate, b.campaign_preface_ab , (SELECT val FROM nv_cd_mst WHERE cd_cat = '100111' AND cd = b.campaign_sts AND lang = #{userVo.language}) campaign_sts_nm , (SELECT val FROM nv_cd_mst WHERE cd_cat = '100121' AND cd = b.approval_sts AND lang = #{userVo.language}) approval_sts_nm , (CASE WHEN b.channel_type = 'm' THEN 4 WHEN b.channel_type = 's' THEN 3 WHEN b.channel_type = 't' THEN 2 ELSE 1 END) seq , c.tag_nm, d.grp_nm, e.name_kor, f.supracampaign_no, '' req_grp_nm FROM nvscenario a, nvcampaign b, nvcamptag c, nvusergrp d, nvuser e, nvschedule f WHERE a.scenario_no = b.scenario_no AND a.tag_no = c.tag_no AND a.grp_cd = d.grp_cd AND a.user_id = e.user_id AND b.campaign_no = f.campaign_no
AND a.scenario_no = #{scenarioNo}
AND b.campaign_no = #{campaignVo.campaignNo}
AND b.depth_no = #{campaignVo.depthNo}
ORDER BY seq DESC

