mapper.select:
SELECT count(*)
FROM nvecaresendlog a, nvecaremsg b
WHERE a.ecare_no = b.ecare_no


AND a.error_cd != '250'


AND a.error_cd = #{errorCd}




AND a.send_dt BETWEEN #{searchQstartDt} AND #{searchQendDt}




AND b.user_id = #{userVo.userId}




AND b.grp_cd LIKE concat(#{userVo.grpCd}, '%')


AND a.customer_key = #{customerKey}
AND a.customer_nm = #{customerNm}
AND a.customer_email = #{customerEmail}
AND a.result_seq = #{resultSeq}
AND a.seq = #{sn}
AND b.ecare_no = #{ecareNo}
AND b.channel_type = #{channel}
AND b.ecare_nm LIKE '%'||#{ecareNm}||'%'

SELECT srfidd
FROM nvecaresendlog
WHERE ecare_no = #{serviceNo}
AND seq = #{seq}

SELECT coalesce(and_send_cnt,0) as and_send_cnt, coalesce(and_succ_cnt,0) as and_succ_cnt, coalesce(and_fail_cnt,0) as and_fail_cnt , coalesce(ios_send_cnt,0) as ios_send_cnt, coalesce(ios_succ_cnt,0) as ios_succ_cnt, coalesce(ios_fail_cnt,0) as ios_fail_cnt , coalesce(etc_send_cnt,0) as etc_send_cnt, coalesce(etc_succ_cnt,0) as etc_succ_cnt , coalesce(etc_req_cnt,0) as etc_req_cnt, coalesce(etc_fail_cnt,0) as etc_fail_cnt , coalesce(and_send_cnt + ios_send_cnt + etc_send_cnt,0) as send_cnt, coalesce(and_succ_cnt + ios_succ_cnt + etc_req_cnt,0) as succ_cnt FROM ( SELECT sum(CASE WHEN send_domain='AND' THEN 1 ELSE 0 END) as and_send_cnt , sum(CASE WHEN send_domain='IOS' THEN 1 ELSE 0 END) as ios_send_cnt , sum(CASE WHEN send_domain='AND' AND error_cd = '000' THEN 1 ELSE 0 END) as and_succ_cnt , sum(CASE WHEN send_domain='IOS' AND error_cd = '000' THEN 1 ELSE 0 END) as ios_succ_cnt , sum(CASE WHEN send_domain='AND' THEN (CASE WHEN error_cd = '000' THEN 0 ELSE 1 END) ELSE 0 END) as and_fail_cnt , sum(CASE WHEN send_domain='IOS' THEN (CASE WHEN error_cd = '000' THEN 0 ELSE 1 END) ELSE 0 END) as ios_fail_cnt , sum(CASE WHEN send_domain='IOS' OR send_domain='AND' THEN 0 ELSE 1 END) as etc_send_cnt , sum(CASE WHEN send_domain='IOS' OR send_domain='AND' THEN 0 ELSE (CASE WHEN error_cd = '000' THEN 1 ELSE 0 END) END) as etc_succ_cnt , sum(CASE WHEN send_domain='IOS' OR send_domain='AND' THEN 0 ELSE (CASE WHEN error_cd = '000' THEN 0 WHEN error_cd = '250' THEN 1 ELSE 0 END) END) as etc_req_cnt , sum(CASE WHEN send_domain='IOS' OR send_domain='AND' THEN 0 ELSE (CASE WHEN error_cd = '000' OR error_cd = '250' OR ERROR_CD = '998' THEN 0 ELSE 1 END) END) as etc_fail_cnt FROM nvecaresendlog WHERE ecare_no = #{service_no}
AND result_seq = #{result_seq}
AND send_dt BETWEEN #{start_dt} AND #{end_dt}
) a

SELECT x.error_cd, x.send_cnt, x.rnum, x.gid , CASE x.rnum WHEN 1 THEN y.val END error_gubun , CASE x.gid WHEN 1 THEN 'Sub Total' WHEN 3 THEN 'Total' ELSE x.val END error_msg FROM ( SELECT c.cd_cat error_gubun, a.error_cd error_cd, count(a.error_cd) send_cnt, max(c.val) error_msg , max(coalesce(c.val, '알수없는 에러코드')) val , max(coalesce(c.val, 'unknown error code')) val , grouping_id(c.cd_cat, a.error_cd) gid , row_number() OVER(PARTITION BY c.cd_cat ORDER BY c.cd_cat) rnum FROM nvecaresendlog a, nvecaremsg b, nv_cd_mst c WHERE a.ecare_no = b.ecare_no AND a.error_cd = c.cd AND a.ecare_no = #{ecare_no}
AND a.result_seq = #{result_seq}
AND a.send_dt = #{send_dt}
AND a.error_cd NOT IN (#{successCode})
AND b.channel_type = 'P' AND c.par_cd_cat LIKE 'AP00%' AND c.lang = #{lang} GROUP BY ROLLUP(c.cd_cat, a.error_cd) ) x LEFT OUTER JOIN nv_cd_mst y ON x.error_gubun = y.cd_cat AND y.par_cd_cat = 'AP00' AND y.lang = #{lang} ORDER BY x.error_gubun, x.gid, x.rnum

SELECT * FROM ( SELECT row_number() OVER (ORDER BY b.send_dt DESC, b.send_tm DESC) as rnum , a.ecare_no, a.ecare_nm, a.service_type, a.sub_type, coalesce(a.resend_ecare_no, 0) as resend_ecare_no, coalesce(b.fail_back_channel, a.channel_type) as msg_type , a.sender_email as sender , a.sender_tel as sender , b.customer_email, b.customer_nm, b.customer_key, b.error_cd, b.err_msg, b.send_dt, b.send_tm, b.result_seq, b.list_seq, b.record_seq , coalesce(b.seq, ' ') as seq, b.customer_key as send_seq, coalesce(b.slot1,' ') as slot1, coalesce(b.slot2,' ') as slot2, coalesce(b.open_dt,'') open_dt , b.part_message as snd_msg, b.req_user_id, b.req_dept_id, b.fail_back_result_cd sms_rslt_cd, c.resend_sts , CASE WHEN b.open_dt IS NULL THEN 'N' ELSE 'Y' END as flag , (SELECT cd_desc FROM nv_cd_mst WHERE cd_cat IN ('AS0001','AS0002','AS0003') AND cd = b.fail_back_result_cd AND lang = #{userVo.language}) as sms_rslt_msg
FROM nvecaremsg a, nvecaresendlog b, nvecaresendresult c WHERE a.ecare_no = b.ecare_no AND b.ecare_no = c.ecare_no AND b.result_seq = c.result_seq
) d WHERE rnum >= ((#{nowPage} - 1) * #{countPerPage} + 1) AND rnum <= #{nowPage} * #{countPerPage}

SELECT
    a.ecare_no as service_no, a.ecare_nm as service_nm, a.service_type, coalesce(a.sub_type,' ') sub_type, a.channel_type
  , b.customer_email, b.customer_nm, b.customer_key, b.error_cd, b.err_msg, b.send_dt, b.send_tm, b.result_seq, b.list_seq, b.record_seq
  , coalesce(b.seq, ' ') as seq, coalesce(b.slot1,' ') as slot1, coalesce(b.slot2,' ') as slot2, b.tmpl_cd
  , coalesce(c.resend_sts,' ') resend_sts, c.super_seq, ' ' as resend_reason, ' ' as ab_test_type
  , coalesce((SELECT file_preview_path FROM nvmobilecontents WHERE conts_no = a.kakao_image_no),' ') as file_preview_path

FROM nvecaremsg a, nvecaresendlog b, nvecaresendresult c
WHERE a.ecare_no = b.ecare_no
AND b.ecare_no = c.ecare_no
AND b.result_seq = c.result_seq
AND a.ecare_no = #{serviceNo}
AND a.channel_type = #{channel}
AND b.result_seq = #{resultSeq}
AND b.list_seq = #{listSeq}

AND b.seq = #{seq}


AND b.customer_key = #{customerKey}

SELECT a.error_cd, b.val as error_desc, count(*) as target_cnt, '' as bounce
FROM nvecaresendlog a, nv_cd_mst b
WHERE a.error_cd = b.cd
AND a.send_dt = #{ecareInfoVo.reportDt}
AND a.ecare_no = #{ecareInfoVo.ecareNo}


AND a.result_seq = #{ecareInfoVo.resultSeq}


AND a.error_cd != '0'
AND b.cd_cat = 'AF0001'
GROUP BY a.error_cd, b.val
ORDER BY a.error_cd

SELECT a.error_cd, b.cd_desc as error_desc, count(a.error_cd) as target_cnt, '' as bounce
FROM nvecaresendlog a, nv_cd_mst b
WHERE a.error_cd = b.cd

AND a.send_dt = #{ecareInfoVo.reportDt}

AND a.ecare_no = #{ecareInfoVo.ecareNo}


AND a.result_seq = #{ecareInfoVo.resultSeq}


AND a.error_cd NOT IN (#{errorCd})
AND b.cd_cat = #{serarchCdCat}
GROUP BY a.error_cd, b.cd_desc
ORDER BY a.error_cd
